function nothing(){}function UnbPopup(url,name,w,h){window.open(url,name,"width="+w+", height="+h+", resizable=yes, scrollbars=yes")}function getel(id){return document.getElementById(id)}var globalKeyHandlers=new Array();function UnbGlobalRegisterKeyHandler(keycode,ascii,flags,funcname,funcparam){var newarr=new Array();newarr["keycode"]=keycode;newarr["ascii"]=ascii;newarr["flags"]=flags;newarr["funcname"]=funcname;newarr["funcparam"]=funcparam;globalKeyHandlers.push(newarr)}function UnbGlobalKeyDispatcher(e){var myFlags;var item;for(var i in globalKeyHandlers){item=globalKeyHandlers[i];myFlags=0;if(e.altKey)myFlags|=1;if(e.ctrlKey)myFlags|=2;if(e.shiftKey)myFlags|=4;if((item["keycode"]&&item["keycode"]==e.keyCode||item["ascii"]&&item["ascii"]==e.which)&&item["flags"]==myFlags){return item["funcname"](e,item["funcparam"])}}}if(navigator.appName!="Microsoft Internet Explorer"){window.captureEvents(Event.KEYPRESS);window.onkeypress=UnbGlobalKeyDispatcher}